    public static String convertJSONToString(Object obj){
        String result= null;
        try {
            ObjectMapper mapper=new ObjectMapper();
            result=mapper.writeValueAsString(obj);

        }
        catch (JsonProcessingException jpe){
            System.out.println(jpe);
        }
        return result;
    }

    @Test
    public void addUser() throws Exception{
        Mockito.when(userService.addUser(any())).thenReturn(user);

        mockMvc.perform(post("http://localhost:8082/api/v1/add-user")
                        .contentType(MediaType.APPLICATION_JSON)
                        .content(convertJSONToString(user)))
                .andExpect(status().isOk()).andDo(MockMvcResultHandlers.print());
    }

